    frst = .true.
    scase = '??'
    sulratw = 2.D0
    sodrat = zero
    crnarat = zero
    crrat = zero
    nofer = 0
    stkofl = .false.
    do i = 1 , nerrmx
      errstk(i) = -999
      errmsg(i) = 'message n/a'
    end do
  end subroutine init1
 
