! ic = m0(12)
! call kmtab(ic ,298.0 ,     xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx
! ,gi0 , &                             xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx
! ,xx) call kmtab(ic ,dble(temp),xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx ,xx
! ,xx ,gii , &                             xx ,xx ,xx ,xx ,xx ,xx ,xx
! ,xx ,xx) m0(12) = m0(12)*exp(ln10*(gi0-gii))
