!
! *** isorropia code
! *** subroutine kmtab
! *** calculates binary activity coefficients by kusik-meissner method.
!     the computations have been performed and the results are stored in
!     lookup tables. the ionic activity 'ionic' is input , and the array
!     'binarr' is returned with the binary coefficients.
!
! *** copyright 1996-2006 , university of miami , carnegie mellon university ,
! *** georgia institute of technology
! *** written by athanasios nenes
! *** updated by christos fountoukis
!
!======================================================================
!
  subroutine kmtab(in,temp,g01,g02,g03,g04,g05,g06,g07,g08,g09,g10,g11, &
                     g12,g15,g16,g17,g18,g19,g20,g21,g22,g23)
    implicit none
    real(rk8) :: g01 , g02 , g03 , g04 , g05 , g06 , g07 , g08 , g09 , &
                 g10 , g11 , g12 , g13 , g14 , g15 , g16 , g17 , g18 , &
                 g19 , g20 , g21 , g22 , g23
    integer(ik4) :: ind
    real(rk8) :: in , temp
    real(rk8) , dimension(23) :: binarray
!
! *** find temperature range
!
    ind = nint((temp-198.0)/25.0) + 1
    ind = min(max(ind,1),6)
!
! *** call appropriate routine
!
    if ( ind==1 ) then
      call km198(in,binarray)
    else if ( ind==2 ) then
      call km223(in,binarray)
    else if ( ind==3 ) then
      call km248(in,binarray)
    else if ( ind==4 ) then
      call km273(in,binarray)
    else if ( ind==5 ) then
      call km298(in,binarray)
    else
      call km323(in,binarray)
    end if
!
    g01 = binarray(01)
    g02 = binarray(02)
    g03 = binarray(03)
    g04 = binarray(04)
    g05 = binarray(05)
    g06 = binarray(06)
    g07 = binarray(07)
    g08 = binarray(08)
    g09 = binarray(09)
    g10 = binarray(10)
    g11 = binarray(11)
    g12 = binarray(12)
    g13 = binarray(13)
    g14 = binarray(14)
    g15 = binarray(15)
    g16 = binarray(16)
    g17 = binarray(17)
    g18 = binarray(18)
    g19 = binarray(19)
    g20 = binarray(20)
    g21 = binarray(21)
    g22 = binarray(22)
    g23 = binarray(23)
  end subroutine kmtab
!
!      integer(ik4) :: function ibacpos(in)
!
!     compute the index in the binary activity coefficient array
