!
!  ater execution of this code segment , the pointer of unit 10 is &
!  pshed to its end.
!
!**********************************************************************
!
  subroutine pushend(iunit)
    implicit none
    integer(ik4) :: iunit , ierr
!
!**********************************************************************
!
    logical opned
!
! *** inquire if iunit connected to file
! ********************************
    inquire (unit = iunit,opened = opned)
    if ( .not.opned ) return
!
