      if ( sign(1.D0,y1)*sign(1.D0,y3)<=zero ) then         ! (y1*y3  <=  zero)
        y2 = y3
        x2 = x3
      else
        y1 = y3
        x1 = x3
      end if
      if ( abs(x2-x1)<=eps*x1 ) go to 200
    end do
    call pusherr(0002,'calci3a')         ! warning error: no convergence
