        dx = (psi4hi-psi4lo)/float(ndiv)
        do i = 1 , ndiv
          x2 = max(x1-dx,psi4lo)
          y2 = funci3b(x2)
          if ( sign(1.D0,y1)*sign(1.D0,y2)<zero ) go to 50  ! (y1*y2 < zero)
          x1 = x2
