 50   do i = 1 , maxit
        x3 = 0.5D0*(x1+x2)
        y3 = funci3b(x3)
        if ( sign(1.D0,y1)*sign(1.D0,y3)<=zero ) then       ! (y1*y3  <=  zero)
          y2 = y3
          x2 = x3
        else
          y1 = y3
          x1 = x3
