!   **********************************************
 100  x3 = 0.5D0*(x1+x2)
      y3 = funci3b(x3)
    end if
!
! *** calculate function value for outer loop
! ***************************
 200  a2 = xk13*(water/gama(13))**5.0
    funci3a = molal(5)*molal(6)*molal(3)**30/a2 - one
