    integer(ik4) :: i
!
! *** setup parameters ************************************************
!
    psi6 = x
    psi1 = chi1
    psi2 = zero
    psi3 = zero
    psi7 = chi7
    psi8 = chi8
    psi9 = chi9
    psi10 = chi10
    psi11 = zero
    frst = .true.
    calain = .true.
!
! *** solve equations ; with iterations for activity coef. ************
!
    do i = 1 , nsweep
!
!   a1  = xk5 *(water/gama(2))**3
      a4 = (xk2/xkw)*r*temp*(gama(10)/gama(5))**2
      a5 = xk4*r*temp*(water/gama(10))**2
      a6 = xk3*r*temp*(water/gama(11))**2
