      kapa = 0.5D0*(-bb+sqrt(dd))
!
!   *** save concentrations in molal array
!   ******************************
      molal(1) = max(lamda+kapa,zero)                        ! hi
      molal(2) = psi2                                       ! nai
      molal(3) = psi1                                       ! nh4i
      molal(4) = zero
      molal(5) = max(kapa+psi4,zero)                         ! so4i
      molal(6) = max(lamda+psi1+psi2+psi3-kapa,zero)         ! hso4i
