!
      call calcmr                                         ! water content
!
!   *** calculate activities or terminate internal loop
!   *****************
      if ( .not.(frst .and. calaou .or. .not.frst .and. calain) ) exit
      call calcact
    end do
!
! *** calculate objective function ************************************
!
    funck3 = molal(9)*molal(6)/a3 - one
  end function funck3
