!
  subroutine calcq3a
    use mod_che_common_isorropia
    use mod_che_common_solut
    implicit none
    real(rk8) :: akw , bb , cc , cli , dd , del , gg , ggcl , ggno3 , &
         hi , hso4i , ohi , psi1o , psi6o , root1 , root3 , so4i
    integer(ik4) :: i , islv
