        call calcmdrp(rh,drmq1,drnh4no3,calcq1a,calcq2a)
        scase = 'q1 ; subcase 3'
      end if
!
    else if ( .not.exno .and. excl ) then ! *** only chloride exists
      if ( rh<drmg2 ) then
        scase = 'q1 ; subcase 1'
        call calcq1a                 ! solid
        scase = 'q1 ; subcase 1'
      else
        scase = 'q1 ; subcase 4'     ! mdrh (nh4)2so4 , na2so4 , nh4cl
        call calcmdrp(rh,drmg2,drnh4cl,calcq1a,calcq3a)
        scase = 'q1 ; subcase 4'
      end if
!
    else if ( rh<drmg3 ) then
      scase = 'q1 ; subcase 1'
      call calcq1a                   ! solid
      scase = 'q1 ; subcase 1'
    else
      scase = 'q1 ; subcase 5'       ! mdrh (nh4)2so4 , na2so4
      call calcmdrp(rh,drmg3,drnh42s4,calcq1a,calcq4)
      scase = 'q1 ; subcase 5'
    end if
  end subroutine calcq1
!
!======================================================================
!
! *** isorropia code
! *** subroutine calcq1a
! *** case q1 ; subcase 1
!
!     the main characteristics of this regime are:
!     1. sulfate poor (sulrat > 2.0) ; sodium poor (sodrat < 2.0)
!     2. solid aerosol only
!     3. solids possible : nh4no3 , nh4cl , (nh4)2so4 , na2so4
!
! *** copyright 1996-2008 , university of miami , carnegie mellon university ,
! *** georgia institute of technology
! *** written by athanasios nenes
!
!======================================================================
!
  subroutine calcq1a
    use mod_che_common_isorropia
    implicit none
    real(rk8) :: frnh3 , frso4
!
! *** calculate solids
! **************************************************
    cna2so4 = 0.5D0*waer(1)
    frso4 = max(waer(2)-cna2so4,zero)
!
    cnh42s4 = max(min(frso4,0.5D0*waer(3)),tiny1)
    frnh3 = max(waer(3)-2.D0*cnh42s4,zero)
!
    cnh4no3 = min(frnh3,waer(4))
! frno3   = max (waer(4)-cnh4no3 , zero)
    frnh3 = max(frnh3-cnh4no3,zero)
!
    cnh4cl = min(frnh3,waer(5))
! frcl    = max (waer(5)-cnh4cl , zero)
    frnh3 = max(frnh3-cnh4cl,zero)
!
! *** other phases
! ******************************************************
    water = zero
!
    gnh3 = zero
    ghno3 = zero
    ghcl = zero
  end subroutine calcq1a
!
!======================================================================
!
! *** isorropia code
! *** subroutine calcr6
! *** case r6
!
!     the main characteristics of this regime are:
!     1. sulfate poor (sulrat > 2.0); sodium rich (sodrat >= 2.0)
!     2. there is only a liquid phase
!
! *** copyright 1996-2008 , university of miami , carnegie mellon university ,
! *** georgia institute of technology
! *** written by athanasios nenes
!
!======================================================================
!
  subroutine calcr6
    use mod_che_common_isorropia
    use mod_che_common_solut
    implicit none
    real(rk8) :: akw , bb , cc , cli , dd , del , gg , ggcl , ggno3 , &
         hi , hso4i , ohi , so4i
    integer(ik4) :: i
    real(rk8) :: nh4i , nai , no3i , nh3aq , no3aq , claq
!
! *** setup parameters ************************************************
!
    call calcr1a
!
    psi1 = cna2so4
    psi2 = cnano3
    psi3 = cnacl
    psi4 = cnh4cl
    psi5 = cnh4no3
!
    frst = .true.
    calain = .true.
    calaou = .true.
!
! *** calculate water **************************************************
!
    call calcmr
!
! *** setup liquid concentrations **************************************
!
    hso4i = zero
    nh3aq = zero
    no3aq = zero
    claq = zero
!
! *** solve equations ; with iterations for activity coef. ************
!
    do i = 1 , nsweep
      akw = xkw*rh*water*water                        ! h2o    <==> h+
!
      nai = waer(1)
      so4i = waer(2)
      nh4i = waer(3)
      no3i = waer(4)
      cli = waer(5)
!
!   soltion acidic or basic?
!
      gg = 2.D0*waer(2) + no3i + cli - nai - nh4i
      if ( gg>tiny1 ) then                        ! h+ in excess
        bb = -gg
        cc = -akw
        dd = bb*bb - 4.D0*cc
        hi = 0.5D0*(-bb+sqrt(dd))
        ohi = akw/hi
      else                                        ! oh- in excess
        bb = gg
        cc = -akw
        dd = bb*bb - 4.D0*cc
        ohi = 0.5D0*(-bb+sqrt(dd))
        hi = akw/ohi
      end if
!
!   undssociated species equilibria
!
      if ( hi<ohi ) then
        call calcamaq2(-gg,nh4i,ohi,nh3aq)
        hi = akw/ohi
      else
        ggno3 = max(2.D0*so4i+no3i-nai-nh4i,zero)
        ggcl = max(gg-ggno3,zero)
        if ( ggcl>tiny1 ) call calcclaq2(ggcl,cli,hi,claq)          ! hcl
        if ( ggno3>tiny1 ) then
          if ( ggcl<=tiny1 ) hi = zero
          call calcniaq2(ggno3,no3i,hi,no3aq)                       ! hno3
        end if
!
!     conentration adjustments ; hso4 minor species.
!
        call calchs4(hi,so4i,zero,del)
        so4i = so4i - del
        hi = hi - del
        hso4i = del
        ohi = akw/hi
      end if
!
!   *** save concentrations in molal array
!   ******************************
      molal(1) = hi
      molal(2) = nai
      molal(3) = nh4i
      molal(4) = cli
      molal(5) = so4i
      molal(6) = hso4i
      molal(7) = no3i
!
!   *** calculate activities or terminate internal loop
!   *****************
      if ( .not.(frst .and. calaou .or. .not.frst .and. calain) ) exit
      call calcact
    end do
! call pusherr (0002 , 'calcr6')    ! warning error: no convergence
!
! *** calculate gas / solid species (liquid in molal already) *********
!
    a2 = (xk2/xkw)*r*temp*(gama(10)/gama(5))**2        ! nh3  <==> nh4+
    a3 = xk4*r*temp*(water/gama(10))**2                ! hno3 <==> no3-
    a4 = xk3*r*temp*(water/gama(11))**2                ! hcl  <==> cl-
!
    gnh3 = nh4i/hi/a2
    ghno3 = hi*no3i/a3
    ghcl = hi*cli/a4
!
    gasaq(1) = nh3aq
    gasaq(2) = claq
    gasaq(3) = no3aq
!
    cnh42s4 = zero
    cnh4no3 = zero
    cnh4cl = zero
    cnacl = zero
    cnano3 = zero
    cna2so4 = zero
  end subroutine calcr6
!
!======================================================================
!
! *** isorropia code
! *** subroutine calcr5
! *** case r5
!
!     the main characteristics of this regime are:
!     1. sulfate poor (sulrat > 2.0); sodium rich (sodrat >= 2.0)
!     2. liquid and solid phases are possible
!
! *** copyright 1996-2008 , university of miami , carnegie mellon university ,
! *** georgia institute of technology
! *** written by athanasios nenes
!
!======================================================================
!
  subroutine calcr5
    use mod_che_common_isorropia
    use mod_che_common_solut
    implicit none
    real(rk8) :: akw , bb , cc , cli , dd , del , gg , ggcl , ggno3 , &
         hi , hso4i , ohi , psio , root , so4i
    integer(ik4) :: i , islv
    logical :: psconv
    real(rk8) :: nh4i , nai , no3i , nh3aq , no3aq , claq
    logical :: nean , neac , nesn , nesc
!
! *** setup parameters ************************************************
!
    call calcr1a                               ! dry solution
!
    nean = cnh4no3<=tiny1        ! nh4no3       ! water exists?
    neac = cnh4cl<=tiny1         ! nh4cl
    nesn = cnano3<=tiny1         ! nano3
    nesc = cnacl<=tiny1          ! nacl
    if ( nean .and. neac .and. nesn .and. nesc ) return
!
    chi1 = cna2so4
!
    psi1 = cna2so4
    psi2 = cnano3
    psi3 = cnacl
    psi4 = cnh4cl
    psi5 = cnh4no3
!
    psio = -great
!
! *** calculate water **************************************************
!
    call calcmr
!
    frst = .true.
    calain = .true.
    calaou = .true.
    psconv = .false.
!
! *** setup liquid concentrations **************************************
!
    nai = waer(1)
    so4i = waer(2)
    nh4i = waer(3)
    no3i = waer(4)
    cli = waer(5)
    hso4i = zero
    nh3aq = zero
    no3aq = zero
    claq = zero
!
! *** solve equations ; with iterations for activity coef. ************
!
    do i = 1 , nsweep
      a5 = xk5*(water/gama(2))**3                   ! na2so4 <==> na+
      akw = xkw*rh*water*water                        ! h2o    <==> h+
!
!   sodum sulfate
!
      root = zero
      if ( nai*nai*so4i>a5 ) then
        bb = -3.D0*chi1
        cc = 3.D0*chi1**2
        dd = -chi1**3 + 0.25D0*a5
        call poly3(bb,cc,dd,root,islv)
        if ( islv/=0 ) root = tiny1
        root = min(max(root,zero),chi1)
        psi1 = chi1 - root
      end if
      psconv = abs(psi1-psio)<=eps*psio
      psio = psi1
!
!   ion concentrations
!
      nai = waer(1) - 2.D0*root
      so4i = waer(2) - root
      nh4i = waer(3)
      no3i = waer(4)
      cli = waer(5)
!
!   soltion acidic or basic?
!
      gg = 2.D0*so4i + no3i + cli - nai - nh4i
      if ( gg>tiny1 ) then                        ! h+ in excess
        bb = -gg
        cc = -akw
        dd = bb*bb - 4.D0*cc
        hi = 0.5D0*(-bb+sqrt(dd))
        ohi = akw/hi
      else                                        ! oh- in excess
        bb = gg
        cc = -akw
        dd = bb*bb - 4.D0*cc
        ohi = 0.5D0*(-bb+sqrt(dd))
        hi = akw/ohi
      end if
!
!   undssociated species equilibria
!
      if ( hi<ohi ) then
        call calcamaq2(-gg,nh4i,ohi,nh3aq)
        hi = akw/ohi
      else
        ggno3 = max(2.D0*so4i+no3i-nai-nh4i,zero)
        ggcl = max(gg-ggno3,zero)
        if ( ggcl>tiny1 ) call calcclaq2(ggcl,cli,hi,claq)          ! hcl
        if ( ggno3>tiny1 ) then
          if ( ggcl<=tiny1 ) hi = zero
          call calcniaq2(ggno3,no3i,hi,no3aq)                       ! hno3
        end if
!
!     conentration adjustments ; hso4 minor species.
!
        call calchs4(hi,so4i,zero,del)
        so4i = so4i - del
        hi = hi - del
        hso4i = del
        ohi = akw/hi
      end if
!
!   *** save concentrations in molal array
!   ******************************
      molal(1) = hi
      molal(2) = nai
      molal(3) = nh4i
      molal(4) = cli
      molal(5) = so4i
      molal(6) = hso4i
      molal(7) = no3i
!
!   *** calculate water
!   **************************************************
      call calcmr
!
!   *** calculate activities or terminate internal loop
!   *****************
      if ( frst .and. calaou .or. .not.frst .and. calain ) then
        call calcact
      else if ( psconv ) then
        exit
      end if
    end do
! call pusherr (0002 , 'calcr5')    ! warning error: no convergence
!
! *** calculate gas / solid species (liquid in molal already) *********
!
    a2 = (xk2/xkw)*r*temp*(gama(10)/gama(5))**2        ! nh3  <==> nh4+
! a21      = xk21*water*r*temp
    a3 = xk4*r*temp*(water/gama(10))**2                ! hno3 <==> no3-
    a4 = xk3*r*temp*(water/gama(11))**2                ! hcl  <==> cl-
!
    gnh3 = nh4i/hi/a2        ! nh4i*ohi/a2/akw
    ghno3 = hi*no3i/a3
    ghcl = hi*cli/a4
!
    gasaq(1) = nh3aq
    gasaq(2) = claq
    gasaq(3) = no3aq
!
    cnh42s4 = zero
    cnh4no3 = zero
    cnh4cl = zero
    cnacl = zero
    cnano3 = zero
    cna2so4 = chi1 - psi1
  end subroutine calcr5
!
!======================================================================
!
! *** isorropia code
! *** subroutine calcr4
! *** case r4
!
!     the main characteristics of this regime are:
!     1. sulfate poor (sulrat > 2.0) ; sodium rich (sodrat >= 2.0)
!     2. solid aerosol only
!     3. solids possible : nh4no3 , nh4cl , na2so4 , nano3 , nacl
!
! *** copyright 1996-2008 , university of miami , carnegie mellon university ,
! *** georgia institute of technology
! *** written by athanasios nenes
!
!======================================================================
!
  subroutine calcr4
    use mod_che_common_isorropia
    implicit none
    logical :: exan , exac , exsn , exsc
!
! *** solve for dry case and see which solids are possible
! **************
    scase = 'r4 ; subcase 2'
    call calcr1a                ! solid
    scase = 'r4 ; subcase 2'
!
    exan = cnh4no3>tiny1        ! nh4no3
    exac = cnh4cl>tiny1         ! nh4cl
    exsn = cnano3>tiny1         ! nano3
    exsc = cnacl>tiny1          ! nacl
!
! *** regime depends on relative humidity and possible species
! **********
    if ( exan .or. exsn .or. exsc ) then   ! *** nh4no3 ,nano3 exist
      if ( rh>=drmh1 ) then
        scase = 'r4 ; subcase 1'
        call calcr4a
        scase = 'r4 ; subcase 1'
      end if
!
    else if ( exac ) then                  ! *** nh4cl exists only
      if ( rh>=drmr5 ) then
        scase = 'r4 ; subcase 3'
        call calcmdrp(rh,drmr5,drnh4cl,calcr1a,calcr5)
        scase = 'r4 ; subcase 3'
      end if
    end if
  end subroutine calcr4
!
!======================================================================
!
! *** isorropia code
! *** subroutine calcr4a
! *** case r4a
!
!     the main characteristics of this regime are:
!     1. sulfate poor (sulrat > 2.0); sodium rich (sodrat >= 2.0)
!     2. liquid and solid phases are possible
!
! *** copyright 1996-2008 , university of miami , carnegie mellon university ,
! *** georgia institute of technology
! *** written by athanasios nenes
!
!======================================================================
!
  subroutine calcr4a
    use mod_che_common_isorropia
    use mod_che_common_solut
    implicit none
    real(rk8) :: akw , bb , cc , cli , dd , del , gg , ggcl , ggno3 , &
         hi , hso4i , ohi , psio1 , psio4 , root , so4i
    integer(ik4) :: i , islv
    logical :: psconv1 , psconv4
    real(rk8) :: nh4i , nai , no3i , nh3aq , no3aq , claq
!
! *** setup parameters ************************************************
!
    frst = .true.
    calain = .true.
    calaou = .true.
    psconv1 = .false.
    psconv4 = .false.
    psio1 = -great
    psio4 = -great
!
! *** calculate initial solution ***************************************
!
    call calcr1a
!
    chi1 = cna2so4          ! salts
    chi4 = cnh4cl
!
    psi1 = cna2so4
    psi2 = cnano3
    psi3 = cnacl
    psi4 = cnh4cl
    psi5 = cnh4no3
!
    call calcmr             ! water
!
    nai = waer(1)           ! liquid concentrations
    so4i = waer(2)
    nh4i = waer(3)
    no3i = waer(4)
    cli = waer(5)
    hso4i = zero
    nh3aq = zero
    no3aq = zero
    claq = zero
!
! *** solve equations ; with iterations for activity coef. ************
!
    do i = 1 , nsweep
      a5 = xk5*(water/gama(2))**3                   ! na2so4 <==> na+
      a14 = xk14*(water/gama(6))**2                 ! nh4cl  <==> nh4+
      akw = xkw*rh*water*water                        ! h2o    <==> h+
!
!   sodum sulfate
!
      root = zero
      if ( nai*nai*so4i>a5 ) then
        bb = -3.D0*chi1
        cc = 3.D0*chi1**2
        dd = -chi1**3 + 0.25D0*a5
        call poly3(bb,cc,dd,root,islv)
        if ( islv/=0 ) root = tiny1
        root = min(max(root,zero),chi1)
        psi1 = chi1 - root
        nai = waer(1) - 2.D0*root
        so4i = waer(2) - root
      end if
      psconv1 = abs(psi1-psio1)<=eps*psio1
      psio1 = psi1
!
!   ammnium chloride
!
      root = zero
      if ( nh4i*cli>a14 ) then
        bb = -(nh4i+cli)
        cc = -a14 + nh4i*cli
        dd = bb*bb - 4.D0*cc
        root = 0.5D0*(-bb-sqrt(dd))
        if ( root>tiny1 ) then
          root = min(root,chi4)
          psi4 = chi4 - root
          nh4i = waer(3) - root
          cli = waer(5) - root
        end if
      end if
      psconv4 = abs(psi4-psio4)<=eps*psio4
      psio4 = psi4
!
      no3i = waer(4)
!
!   soltion acidic or basic?
!
      gg = 2.D0*so4i + no3i + cli - nai - nh4i
      if ( gg>tiny1 ) then                        ! h+ in excess
        bb = -gg
        cc = -akw
        dd = bb*bb - 4.D0*cc
        hi = 0.5D0*(-bb+sqrt(dd))
        ohi = akw/hi
      else                                        ! oh- in excess
        bb = gg
        cc = -akw
        dd = bb*bb - 4.D0*cc
        ohi = 0.5D0*(-bb+sqrt(dd))
        hi = akw/ohi
      end if
!
!   undssociated species equilibria
!
      if ( hi<ohi ) then
        call calcamaq2(-gg,nh4i,ohi,nh3aq)
        hi = akw/ohi
      else
        ggno3 = max(2.D0*so4i+no3i-nai-nh4i,zero)
        ggcl = max(gg-ggno3,zero)
        if ( ggcl>tiny1 ) call calcclaq2(ggcl,cli,hi,claq)          ! hcl
        if ( ggno3>tiny1 ) then
          if ( ggcl<=tiny1 ) hi = zero
          call calcniaq2(ggno3,no3i,hi,no3aq)                       ! hno3
        end if
!
!     conentration adjustments ; hso4 minor species.
!
        call calchs4(hi,so4i,zero,del)
        so4i = so4i - del
        hi = hi - del
        hso4i = del
        ohi = akw/hi
      end if
!
!   *** save concentrations in molal array
!   ******************************
      molal(1) = hi
      molal(2) = nai
      molal(3) = nh4i
      molal(4) = cli
      molal(5) = so4i
      molal(6) = hso4i
      molal(7) = no3i
!
!   *** calculate water
!   **************************************************
      call calcmr
!
!   *** calculate activities or terminate internal loop
!   *****************
      if ( frst .and. calaou .or. .not.frst .and. calain ) then
        call calcact
      else if ( psconv1 .and. psconv4 ) then
        exit
      end if
    end do
! call pusherr (0002 , 'calcr4a')    ! warning error: no convergence
!
! *** calculate gas / solid species (liquid in molal already) *********
!
    a2 = (xk2/xkw)*r*temp*(gama(10)/gama(5))**2       ! nh3  <==> nh4+
    a3 = xk4*r*temp*(water/gama(10))**2               ! hno3 <==> no3-
    a4 = xk3*r*temp*(water/gama(11))**2               ! hcl  <==> cl-
!
    gnh3 = nh4i/hi/a2
    ghno3 = hi*no3i/a3
    ghcl = hi*cli/a4
!
    gasaq(1) = nh3aq
    gasaq(2) = claq
    gasaq(3) = no3aq
!
    cnh42s4 = zero
    cnh4no3 = zero
    cnh4cl = chi4 - psi4
    cnacl = zero
    cnano3 = zero
    cna2so4 = chi1 - psi1
  end subroutine calcr4a
