        psi1 = chi1 - root1
      end if
      psconv1 = abs(psi1-psi1o)<=eps*psi1o
      psi1o = psi1
!
!   ammnium sulfate
!
      if ( nh4i*nh4i*so4i>a6 ) then
        bb = -((waer(2)-waer(6))+waer(3)-root7-root1)
