      scase = 'v2 ; subcase 2'
      call calcv1a                   ! solid
      scase = 'v2 ; subcase 2'
    else
      scase = 'v2 ; subcase 4'       ! mdrh caso4 , (nh4)2so4 , mgso4 , na2so4 , k2so4
      call calcmdrpii(rh,drmo3,drnh42s4,calcv1a,calcv4)
      scase = 'v2 ; subcase 4'
