  subroutine calcv2a
    use mod_che_common_isorropia
    use mod_che_common_solut
    implicit none
    real(rk8) :: akw , bb , cc , cli , dd , del , gg , hi , hso4i , &
         ohi , psi1o , psi40 , psi60 , psi70 , root1 , root4 , root4a , &
         root4b , root6 , root7
    real(rk8) :: so4i
    integer(ik4) :: i , islv
    logical :: psconv7 , psconv1 , psconv6 , psconv4
