         ohi , psi13o , psi9o , root13 , root13a , root13b , root9 , &
         so4i , kcl
    integer(ik4) :: i , islv
    logical :: psconv9 , psconv13
    real(rk8) :: nh4i , nai , no3i , nh3aq , no3aq , claq , cai , ki , mgi
!
! *** setup parameters ************************************************
!
    frst = .true.
    calain = .true.
    calaou = .true.
!
