        root9 = max(root9,zero)
        psi9 = chi9 - root9
      end if
      psconv9 = abs(psi9-psi9o)<=eps*psi9o
      psi9o = psi9
!
!   potssium nitrate
!
      if ( ki*no3i>a13 ) then
        bb = -(waer(4)+waer(7)-2.D0*root9)
        cc = waer(4)*(waer(7)-2.D0*root9) - a13
        dd = sqrt(max(bb*bb-4.D0*cc,zero))
        root13a = 0.5D0*(-bb-dd)
        root13b = 0.5D0*(-bb+dd)
        if ( zero<=root13a ) then
          root13 = root13a
        else
          root13 = root13b
