!   ion concentrations ; corrections
!
      ki = max(waer(7)-2.D0*root9-root13,zero)
      so4i = max(waer(2)-waer(6)-root9,zero)
      nh4i = waer(3)
      no3i = max(waer(4)-root13,zero)
      cli = waer(5)
      cai = zero
      nai = waer(1)
      mgi = waer(8)
