    cnh4no3 = zero
    cnh4cl = zero
    cnacl = zero
    cnano3 = zero
    cmgso4 = zero
    ck2so4 = chi9 - psi9
    ccaso4 = min(waer(6),waer(2))
    ccano32 = zero
    ckno3 = chi13 - psi13
    kcl = zero
    cmgno32 = zero
    cmgcl2 = zero
    ccacl2 = zero
  end subroutine calcw10
